import \'package:flutter/material.dart\';\nimport \'package:intl/intl.dart\';\nimport \'package:listynest/services/bidding_service.dart\';\n\nclass RealTimeBiddingCard extends StatelessWidget {\n  final Bid bid;\n\n  const RealTimeBiddingCard({Key? key, required this.bid}) : super(key: key);\n\n  @override\n  Widget build(BuildContext context) {\n    final currencyFormat = NumberFormat.simpleCurrency(locale: \'en_US\');\n\n    return Card(\n      elevation: 2,\n      margin: const EdgeInsets.symmetric(horizontal: 16, vertical: 8),\n      child: ListTile(\n        leading: CircleAvatar(\n          child: Icon(Icons.gavel),\n        ),\n        title: Text(\n          \'New Bid: \${currencyFormat.format(bid.amount)}\',\n          style: TextStyle(fontWeight: FontWeight.bold),\n        ),\n        subtitle: Text(\'on ad: \${bid.adId} by \${bid.bidder}\'),\n        trailing: Icon(Icons.arrow_forward),\n      ),\n    );\n  }\n}\n